int ScrollP(lp,sc,ks,opt)
SCRSTRU *sc;
LISTSTRU *lp;
int ks;
int opt;
{
  int i,dofile,ret;
  FSELSTRU *fs;

  ret = 1;
  fs = NULL;

  if (lp != NULL) {
	if (lp == &fsel.flist || lp == &fsel.dirlist) {
	   fs = &fsel;
	} else if (lp == &dsel.flist || lp == &dsel.dirlist) {
	   fs = &dsel;
	} else if (lp == &gsel.flist || lp == &gsel.dirlist) {
	   fs = &gsel;
	} else if (lp->win == MLTwin) {
	   ret = 0;
	} else if (lp->win == ZMEwin) {
	   ret = 0;
	} else {
	   for (i=0; i<MXSHFILS; i++) {
		if (lp == &shfiles[i].FILElist) ret = 0;
	   }
	   if (ret) return(0);
	}
  }
  dofile = 0;

  switch (opt) {
  case 0:
	*sc->wpos = 0;
	sc->fpos = 0;
	break;
  case 1:
	*sc->wpos = *sc->wpos + sc->wh;
	sc->fpos = 0;
	break;
  case 2:
	*sc->wpos = *sc->wpos - sc->wh;
	sc->fpos = 0;
	break;
  case 3:
	*sc->wpos = *sc->wn - sc->wh;
	sc->fpos = 0;
	break;
  case 4:
	{
	int wh;
	wh = sc->wh;
	if (*sc->wn < wh) wh = *sc->wn;
	*sc->wpos = ks;
	sc->fpos = 0;
	if (*sc->wpos > (*sc->wn) - 1 - wh) {
	   *sc->wpos = (*sc->wn - wh);
	   sc->fpos = ks - *sc->wpos;
	}
	selfil = *sc->wpos + sc->fpos;
	}
	break;
  case 5:
	{
	int wh;
	wh = sc->wh;
	if (*sc->wn < wh) wh = *sc->wn;
	if (*sc->wpos > (*sc->wn) - 1 - wh) {
	   sc->fpos = sc->fpos - 1;
	   if (sc->fpos < 0) {
		sc->fpos = 0;
		*sc->wpos = *sc->wpos - 1;
	   }
	} else {
	   *sc->wpos = *sc->wpos - 1;
	}
	selfil = *sc->wpos + sc->fpos;
	}
	break;
  case 6:
	{
	int wh;
	wh = sc->wh;
	if (*sc->wn < wh) wh = *sc->wn;
	if (*sc->wpos > (*sc->wn) - 1 - wh) {
	   *sc->wpos = (*sc->wn - wh);
	   sc->fpos = sc->fpos + 1;
	   if (sc->fpos > wh - 1)  sc->fpos = wh - 1;
	} else {
	   *sc->wpos = *sc->wpos + 1;
	}
	selfil = *sc->wpos + sc->fpos;
	}
	break;
  case 7:
	if (fs == &fsel) dofile = 1;
	break;
  default:
	break;
  }

  if (opt == 4 || opt == 5 || opt == 6 || opt == 7) {
     *sc->wpos = *sc->wpos + sc->fpos;
     if (*sc->wpos > (*sc->wn) - 1 ) *sc->wpos = (*sc->wn) - 1;
  } else {
     if (*sc->wpos > (*sc->wn) - 1 - sc->wh) {
	if ((*sc->wn) - 1 - sc->wh >= 0) {
	   *sc->wpos = (*sc->wn) - sc->wh;
	}
	else *sc->wpos = 0;
     }
  }
  if (*sc->wpos < 0) *sc->wpos = 0;
  lp->select = *sc->wpos;

  if (fs != NULL) parname(fs,strname,lp->select);

  if (dofile) {
     if (lp == &fsel.flist) {
	if (ParseFile(strname,fs->files[lp->select])) return(1);
     } else {
	NewDir(fs);
	getfiles(fs,fs->lastdir);
	RedrawFwin(fs);
	update_model = 0;
     }
  } else if (opt == 7 && fs != NULL) {
    str2qbox(&qboxes[QREADF],fs->files[selfil],-1);
    PromptBox(&qboxes[QREADF]);
    if (fs->subopt == 1) {
	str2qbox(&qboxes[QMFILE],fs->files[selfil],-1);
        PromptBox(&qboxes[QMFILE]);
    } else if (fs->subopt == 2) {
	str2qboxa(&qboxes[QQSAR],fs->files[selfil],fs->lastdir,-1);
        PromptBox(&qboxes[QQSAR]);
    } else if (fs->subopt == 3) {
	str2qboxa(&qboxes[QQSARB],fs->files[selfil],fs->lastdir,-1);
        PromptBox(&qboxes[QQSARB]);
    } else if (fs->subopt == 4) {
	str2qbox(&qboxes[QQUERY],fs->files[selfil],-1);
        PromptBox(&qboxes[QQUERY]);
	fs->qbrfile = 0;
    }
    XUnmapWindow(display,fs->win);
    return(0);
  }
  
  if (lp != NULL) {
	if (lp->win == ZMEwin) {
		UpdateZME();
		RedrawScroll(sc);
	} else {
	   RedrawList(lp);
	}
	if (fs != NULL) RedrawFwinH(fs);
  } else if (sc != NULL) {
	RedrawScroll(sc);
  }
  XSync(display,True);

  return(0);

}

