void TrmStr(qp)
QBOXSTRU        *qp;
{
  int len,i,j;

  len = strlen(qp->str);

  j = len;
  for (i=len-1; i >= 0; i--) {
	if (qp->str[i] != ' ') {
	   j = i+1;
	   break;  
	}
  }
  
  len = j;
  qp->str[len] = '\0';
}

void molcopy(obj, cop, len)
     const char *obj;
     char *cop;
     int len;
{

  if (obj == cop || len <= 0) return;

  if (obj < cop && obj+len > cop) { 

    obj = obj + len - 1;
    cop = cop + len - 1;
    for ( ; len>0; len--, obj--, cop--) *cop = *obj;

  } else { 

    for ( ; len>0; len--, obj++, cop++) *cop = *obj;

  }
}

int DirKey(qp,c,ks)
QBOXSTRU	*qp;
int c;
KeySym ks;
{
  int len, retval;

  if(DEBUG)fprintf(stderr,"DirKey in\n");

  if (qp->fake) return(-1);

  retval = -1;
  if (qp->str == NULL) {
	len = 0;
  } else {
	len = strlen(qp->str);
  }
  if (qp->curpos < 0) qp->curpos = 0;
 
  if (qp->qbopt > QSTRING ) { 
    if ((c>='\060' && c<='\071') || (c=='+' && qp->qbopt == QREAL) 
    || (c=='-' && qp->qbopt == QREAL) || (c=='.' && qp->qbopt >= QPOSREAL)) {
            if (len >= qp->strmax) {
		if (*ibell) XBell(display,0); 
		return(-1);
	    }
	    molcopy(&qp->str[qp->wnd_start+qp->curpos],
		    &qp->str[qp->wnd_start+qp->curpos+1],
		    (len-(qp->wnd_start+qp->curpos)+1));
            qp->str[qp->wnd_start+qp->curpos]=c; retval = 0;
	    qp->curpos++;
    }
  }
  else 
  {
   if (c>=' ' && c<'\177') { 
    if (len >= qp->strmax) {
	if (*ibell) XBell(display,0); 
	return(-1);
    }
    molcopy(&qp->str[qp->wnd_start+qp->curpos],
	    &qp->str[qp->wnd_start+qp->curpos+1],
	    (len-(qp->wnd_start+qp->curpos)+1));
    qp->str[qp->wnd_start+qp->curpos]=c; retval = 0;
    qp->curpos++;
   }
  }

  if (ks==XK_Left) {
        if (qp->curpos > 0) {
	   qp->curpos--; retval = 0;
	} else {
	   if (qp->wnd_start > 0) {
		qp->wnd_start--; retval = 0;
	   }
	}
  }

  if (ks==XK_Right) {
        if (qp->curpos == qp->wlen) {
	   if (qp->wnd_start < (qp->strmax - qp->wlen)) {
	      if (strlen(qp->str) > qp->wlen) {
	      if (qp->wnd_start < (strlen(qp->str) - qp->wlen)) {
		if (qp->wnd_start < strlen(qp->str)) {
		   qp->wnd_start++; retval = 0;
		}
	      }
	      }
	   }
	} else if (qp->curpos <= qp->wlen && qp->curpos < strlen(qp->str)) {
	   qp->curpos++; retval = 0;
	}
  }

  if (retval) {
     if (c=='\010' && qp->curpos > 0) {
        if (len==0) {
	   if (*ibell) XBell(display,0); 
	   return(-1);
	}
	molcopy(&qp->str[qp->wnd_start+qp->curpos],
		&qp->str[qp->wnd_start+qp->curpos-1],
		(len-(qp->wnd_start+qp->curpos)+1));
	qp->curpos--;
     }

     else if (c=='\004' || c=='\177') {
        if (len==0) {
	   if (*ibell) XBell(display,0); 
	   return(-1);
	}
	molcopy(&qp->str[qp->wnd_start+qp->curpos+1],
		&qp->str[qp->wnd_start+qp->curpos],
		(len-(qp->wnd_start+qp->curpos)));
     }

     else if (c=='\025' || c=='\013') {
       qp->str[0] = '\0';
     }

     else if (c=='\012' || c=='\015') {
       return(-2);
     }

     else {
	if (*ibell) XBell(display,0); 
	return(-1);
     }

  }

  PromptBox(qp);
  return(0);
}

int ChkKey(qp,c)
QBOXSTRU	*qp;
int c;
{
  int retval;

  if (qp->fake) return(-1);

  retval = -1;
 
  if (qp->qbopt > QSTRING ) { 
    if ((c>='\060' && c<='\071') || (c=='+' && qp->qbopt == QREAL) 
    || (c=='-' && qp->qbopt == QREAL) || (c=='.' && qp->qbopt >= QPOSREAL))
             retval = 0;
  } else if (c>=' ' && c<'\177') retval = 0;

  return(retval);
}

