void spa_not_substr(char *fname,char *substr)
{
   int i,n,ns,ss;
   char *ptr1,ptr2;

   if (fname == NULL) return;
   if (substr == NULL) return;

   n = strlen(fname);
   ns = strlen(substr);
   ptr1 = myStrcasestr(fname,substr);
   ss = (int) (ptr1 - fname);
   
   for (i = 0; i < ss; i++) {
	fname[i] = ' ';
   }

   for (i = ss+ns; i < n; i++) {
	fname[i] = ' ';
   }
}

