int PDBFile(strname)
char *strname;
{
   int naat,size;
   struct stat st_record;

   stat(strname, &st_record);
   size = (int) st_record.st_size;
   if (size <= 1) {
	fprintf(stderr,"Empty file !\n");
	return(0);
   }

   naat = 0;

   if (!IsBinary(strname)) {
	int ispdb,i;
	FILE *f;
	char cmdfil[MAXCMD];

	ispdb = 0;
	f = fopen(strname,"r");

	for (i = 0; i < 10; i++) {       
	   fgets(cmdfil,MAXCMD,f);
           if (strstr(cmdfil,"HEADER")) ispdb = 1;

	}

	if (ispdb) {
	   rewind(f);
	   while (1) {
		if (fgets(cmdfil,MAXCMD,f) == NULL) break;
		if (strstr(cmdfil,"ATOM") || strstr(cmdfil,"HETATM")) naat++;
	   }
	   naat = naat * 3;
	}
	fclose(f);
   }
   return(naat);
}

int NAtMax(strname)
char *strname;
{
   int mx;

   mx = 0;

   if (!IsBinary(strname)) {
	int i,mxh;
	FILE *f;
	char cmdfil[MAXCMD];

	f = fopen(strname,"r");

	rewind(f);
	mx = 0;
	while (1) {
		if (fgets(cmdfil,MAXCMD,f) == NULL) break;
		if (strstr(cmdfil,"V2000")) {
		    sscanf(cmdfil,"%3d",&mxh);
		    if (mxh > mx) mx = mxh;
		}
	}
	fclose(f);
   }
   return(mx);
}

