void CentLig()
{
      int i,l,m;
      double CoorLig[3][MXHETA]= {0.0};
      double dmn[MXHETA]= {0.0};
      double d2,dx;
      int NligAt[MXHETA] = {0};

      if (*ipdbon && !clfstrptr->ilcset) {

	for (l=0; l < *xyzp->iatoms; l++) {

	  if (xyzp->iresid[l] < -3) {

	   m = ABS(xyzp->iresid[l]);
	   
	   if (m < MXHETA) {
		for (i=0; i<3; i++) 
		   CoorLig[i][m] = CoorLig[i][m] + xyzp->coo[l*3+i];
		NligAt[m]++;
	   }

	  }
	}

	for (l=0; l < MXHETA; l++) {
	   if (NligAt[l] > 0) {
		for (i=0; i<3; i++) 
			CoorLig[i][l] = CoorLig[i][l] / (NligAt[l]);
	   }
	   dmn[l] = 1.0e08;
	}

	for (l=0; l < *xyzp->iatoms; l++) {

	  if (xyzp->iresid[l] < -3) {

	   m = ABS(xyzp->iresid[l]);
	   

	   if (m < MXHETA) {
	      d2 = 0.0;
	      for (i = 0; i < 3; i++) {
		dx = CoorLig[i][m] - xyzp->coo[l*3+i];
		d2 = d2 + dx*dx;
	      }

	      if (d2 < dmn[m])  {
		dmn[m] = d2;
		clfstrptr->ligcat[m] = l;
	      }
	   }

	  }
	}
	clfstrptr->ilcset = 1;

      }


}


