int getobfp()
{
  char cmdfil[MAXPATHLEN];
  char word[MAXPATHLEN];
  FILE *fpout;
  int i,len;

  if ( access("/tmp/obfp",F_OK) == 0 && !isdir("/tmp/obfp") ) {
	unlink("/tmp/obfp");
  }

  for (i = 0; i < MAXOBFP; i++) {
	obfpptr[i] = NULL;
  }
  nfps = -1;

  if (OBloc == 1) {
	sprintf(cmdfil,
	   "%sobabel -L fingerprints > /tmp/obfp 2>&1",
	   exepath[0]);

  } else if (OBloc == 2) {
	sprintf(cmdfil,
	   "/usr/bin/obabel -L fingerprints > /tmp/obfp 2>&1");
  } else {
	sprintf(cmdfil,
	   "/usr/local/bin/obabel -L fingerprints > /tmp/obfp 2>&1");
  }

  system(cmdfil);

  if ( access("/tmp/obfp",F_OK) == 0 && !isdir("/tmp/obfp") ) {
	fpout = fopen("/tmp/obfp","r");
	if (fpout != NULL) {

	   while (fgets(cmdfil,MAXCMD,fpout) != NULL) {
		sscanf(cmdfil,"%s",(char *) &word);
		if (nfps < MAXOBFP) {
		   nfps++;
		   len = strlen(word);
		   obfpptr[nfps] = (char *) malloc(sizeof(char)*(len+1));
		   strcpy(obfpptr[nfps],word);
		}
	   }
	   nfps++;

	   for (i = 0; i < nfps; i++) {
		if (strstr(obfpptr[i],"MACCS")) fpsopt = i;
	   }

	   if (fpsopt != -1) {
		fprintf(stderr,"chosen fingerprint: %s\n",obfpptr[fpsopt]);
	   } else {
		for (i = 0; i < nfps; i++) {
		   if (strstr(obfpptr[i],"FP3")) fpsopt = i;
		}

		if (fpsopt != -1) {
		   fprintf(stderr,"chosen fingerprint: %s\n",obfpptr[fpsopt]);
		} else {
		   if (nfps > 0) {
		      fpsopt = nfps -1;
		      fprintf(stderr,"chosen fingerprint: %s\n",
				obfpptr[fpsopt]);
		   } else {
		      fprintf(stderr,"NO fingerprint available\n");
		   }
		}

	   }

	   unlink("/tmp/obfp");

/*
	   for (i = 0; i < nfps; i++) 
		fprintf(stderr,"fingerprint: %d %s\n",i,obfpptr[i]);
*/
	}
  } else {
     return(0);
  }

}

int getob()
{
  char cmdfil[MAXSTRLEN];
  char cmdfil2[MAXSTRLEN];
  char resolved[MAXSTRLEN];
  char *ptr;

  realpath(exepath[0],resolved);
  sprintf(cmdfil,"%s/obabel",resolved);
  if (access(cmdfil,X_OK) == 0 && !isdir(cmdfil)) OBloc = 1;

  strcpy(cmdfil,"/usr/local/lib/openbabel");
  strcpy(cmdfil2,"/usr/lib/openbabel");
  if ((!isdir(cmdfil) && !isdir(cmdfil2)) || OBloc == 1) {
     if (getenv("BABEL_LIBDIR") == NULL) {
#if !defined(DARWIN)
	sprintf(cmdfil,"%s",exepath[0]);
	realpath(exepath[0],resolved);
	setenv("BABEL_LIBDIR",resolved,1);
	setenv("BABEL_DATADIR",resolved,1);
#else
	sprintf(cmdfil,"%s/data",exepath[0]);
	setenv("BABEL_DATADIR",cmdfil,1);
	sprintf(cmdfil,"%s/lib",exepath[0]);
	setenv("BABEL_LIBDIR",cmdfil,1);
#endif
     }
  }

  OBloc = -1;
  sprintf(cmdfil,"%sobabel",exepath[0]);
  if (access(cmdfil,X_OK) == 0 && !isdir(cmdfil)) {
	OBloc = 1;
	getobfp();
	return(1);
  }

  strcpy(cmdfil,"/usr/local/bin/obabel");
  if (access(cmdfil,X_OK) == 0 && !isdir(cmdfil)) {
	OBloc = 0;
	getobfp();
	return(1);
  }

  strcpy(cmdfil,"/usr/bin/obabel");
  if (access(cmdfil,X_OK) == 0 && !isdir(cmdfil)) {
	OBloc = 2;
	getobfp();
	return(1);
  }

  fprintf(stderr,"Could NOT locate obabel executable !\n");

  return(0);
}

