void getHConN(int *nhs,int *hns, int ires, int *nh)
{
int ica2,jj,i;

nhs[0] = -1; nhs[1] = -1;
hns[0] = -1; hns[1] = -1;

IRES = ires;
*nh = calfptr->icalf[ires-1][0] - 1;
ica2 = calfptr->icalf[ires][0] - 1;

if (ires >= calfptr->ncalf) ica2 = *zmptrp->nz -1;

	for (i=*nh+3; i < ica2+1; i++) {

		if (xyzp->iresid[i] == ires) {

			if (zmptrp->ianz[i] == 7) {
			   if (nhs[0] == -1) {
				nhs[0] = i;
			   } else if (nhs[1] == -1) {
				nhs[1] = i;
			   }
			} else if (zmptrp->ianz[i] == 1) {

			   if (xyzp->iconn[i*(MXCON+1)] == 1) {
				jj = xyzp->iconn[i*(MXCON+1)+1];
				if (jj-1 != 0) {
				   if (zmptrp->ianz[jj-1] == 7) {
					if (nhs[0] == jj-1) {
					   hns[0] = i;
					} else if (nhs[1] == jj-1) {
					   hns[1] = i;
					}
				   }
				}
			   }
			}
		}
	}
}

