void ActualiseBackbone(int iset)
{
   int j;

   if (*backb) {
	TogDown(&cbut[BBACKB]);
	ActBut(&cbut[BFORC],1);
	ActBut(&cbut[BINCRF],1);
	ActBut(&cbut[BHBOND],1);
	ActBut(&cbut[BDECRF],1);
	ActBut(&cbut[BUNSC],1);
	preset(iset);
   } else {
	TogUp(&cbut[BBACKB]);
	ActBut(&cbut[BFORC],0);
	ActBut(&cbut[BINCRF],0);
	ActBut(&cbut[BDECRF],0);
	ActBut(&cbut[BUNSC],0);
	ActBut(&cbut[BHBOND],0);
	if (iset) {
	   for (j=0; j < MXHETA; j++) 
		calfptr->ihet[j] = 0;
	   for (j=0; j < calfptr->ncalf; j++) 
		calfptr->reson[j] = 1;
	   for (j=0; j < *xyzp->iatoms; j++ ) 
		xyzp->iaton[j] = 1;
	}
	update_sel = 1;
   }
}

void DoBackbone()
{
   int j;

   TogBut(&cbut[BBACKB]);
   if (!*backb) {
	*backb = 1; 
	*ionoff = 1;
	ActBut(&cbut[BFORC],1);
	ActBut(&cbut[BINCRF],1);
	ActBut(&cbut[BHBOND],1);
	ActBut(&cbut[BDECRF],1);
	ActBut(&cbut[BUNSC],1);
	preset(1);
   } else {
	*backb = 0;
	ActBut(&cbut[BFORC],0);
	ActBut(&cbut[BINCRF],0);
	ActBut(&cbut[BDECRF],0);
	ActBut(&cbut[BUNSC],0);
	ActBut(&cbut[BHBOND],0);
	for (j=0; j < MXHETA; j++) 
	   calfptr->ihet[j] = 0;
	for (j=0; j < calfptr->ncalf; j++) 
	   calfptr->reson[j] = 1;
	for (j=0; j < *xyzp->iatoms; j++ ) 
	   xyzp->iaton[j] = 1;
	update_sel = 1;
   }
}

