	Tri->CenO[i] = 0.0;
	Tri->Out[i] = 0.0;
	Tri->OutO[i] = 0.0;
	Tri->OutOrg[i] = 0.0;
	Tri->t[i] = 0.0;
	Tri->lig[i] = 0;
	Tri->ohlig[i] = 0;
	Tri->prot[i] = 0;
	Tri->oh[i] = NULL;
