double dst(Tri1,Tri2)
TRISTRU *Tri1;
TRISTRU *Tri2;
{
   int i,j;
   double dd,tt;

   dd = 0.0;

   for (i = 0; i < ndocka; i++ ) {
      for (j = 0; j < 3; j++ ) {
	tt = (Tri1->coo[i*3+j] - Tri2->coo[i*3+j]);
	dd = dd + tt*tt;
      }
   }
   dd = dd / ndocka;
   dd = sqrt(dd);
   return(dd);
}

