void set_List(node *begin,node **barr,int *N,int MXARR)
{
    node *p;
    
    *N = 0;
    if (begin == NULL) return;

    p = begin;

    while(p != NULL) {
	barr[*N] = p;
        p = p->next_node;
	(*N)++;
	if (*N == MXARR) return;
    }
}

int max_List(node *begin)
{
    node *p;
    LDAT info;
    int mx;
    
    mx = 0;
    if (begin == NULL) return(mx);

    p = begin;
    info = p->info;
    mx = info.i;
    if (info.j > mx) mx = info.j;

    while(p != NULL) {
	info = p->info;
	if (info.i > mx) mx = info.i;
	if (info.j > mx) mx = info.j;
        p = p->next_node;
    }

    return(mx);
}

void upd_List(node **barr,int *N,node *delnode)
{
    int i,j;

    for (i=0; i < *N; i++) {
	if (barr[i] == delnode) {
	   (*N)--;
	   for (j=i; j < *N; j++) {
		barr[j] = barr[j+1];
	   }
	}
    }
}

void replc_List(node *begin,int val,int nval)
{
    int n,i,j;
    node *p;
    
    n = 0;

    if (begin==NULL) {
        return;
    }

    p = begin;
    while (p != NULL) {
	i = p->info.i; j = p->info.j;
	if (i == val || j == val) {
	   if (i == val) i = nval;
	   if (j == val) j = nval;
	   if (i < j) {
		   p->info.i = i;
		   p->info.j = j;
	   } else {
		   p->info.i = j;
		   p->info.j = i;
	   }
	}
        p=p->next_node;
	n++;
    }
}
 
