node *insertdd(node *begin,node **barr,int *N,LDAT data_element)
{
    node *temp,*p,*tp,*tn;
    int n;

    temp = (node *) malloc(sizeof(node));
    temp->info = data_element;
    temp->next_node = NULL;
    temp->prev_node = NULL;

    p = begin;
    tp = NULL;

    n = 0;
    while (p != NULL) {
        if (p->next_node == NULL)  {

            temp->prev_node=p;
            temp->next_node=p->next_node;
            p->next_node = temp;
	    goto NEXT;

	} else if (p->prev_node == NULL) {
	   if (p->info.dd < data_element.dd) {
              temp->prev_node = NULL;
              temp->next_node= p;
              p->prev_node = temp;
	      begin = temp;
	      goto NEXT;
	   }

	} else {

	   if (p->info.dd < data_element.dd && 
	    p->next_node->info.dd >= data_element.dd) {

            temp->prev_node=p;
            temp->next_node=p->next_node;

            p->next_node->prev_node = temp;

            p->next_node = temp;
	    goto NEXT;
           }

        }
	tp = p;
        p = p->next_node;
	n++;
    }
NEXT:
    return begin;
}
 
node *deletenode(node *begin,node *delnode,LDAT *data_element,node **barr,int *N)
{
    node *temp,*tp,*tn;

    if (begin == NULL) {
	goto NEXT;
    }

    if (begin->next_node == NULL) {  
        if (begin->info.i == data_element->i && 
		begin->info.j == data_element->j) {
            temp  = begin;
            begin = NULL;
	    data_element->dd = temp->info.dd;
	    delnode = begin;

        } else {
	    delnode = NULL;

        }
	goto NEXT;
    }
    

    if (begin->info.i == data_element->i && 
	begin->info.j == data_element->j) {

        temp=begin;
	data_element->dd = temp->info.dd;
        begin=begin->next_node;  
        begin->prev_node=NULL;
	delnode = begin;
	goto NEXT;
    }
 
    temp=begin->next_node;
    tp = begin;
    tn = temp->next_node;

    while (temp->next_node != NULL) {

        if (temp->info.i == data_element->i && 
		temp->info.j == data_element->j) {

            if (tp != NULL) {
		if (tp->next_node != NULL) {
		   tp->next_node = temp->next_node;
		}
	    }
            if (tn != NULL) {
		tn->prev_node = temp->prev_node;
	    }
	    delnode = temp;
	    goto NEXT;
        }
	tp = temp;
        temp=temp->next_node;
	tn = temp->next_node;
    }
 
    if (temp->info.i == data_element->i && 
	temp->info.j == data_element->j) {
	data_element->dd = temp->info.dd;

        temp->prev_node->next_node=NULL;
	delnode = temp;
	goto NEXT;
    }

    delnode = NULL;
NEXT:
    upd_List(barr,N,delnode);
    return begin;
}
 
node *dlnode(node *p,LDAT *data_element)
{
    node *temp,*tp,*tn,*ipp;
    int ip = 0;

    if (p == NULL) {
	goto NEXT;
    }

    if (p->next_node == NULL) {  
        if (p->info.i == data_element->i && 
		p->info.j == data_element->j) {
            temp  = p;
            p = NULL;
	    data_element->dd = temp->info.dd;
        }
	goto NEXT;
    }
    

    if (p->info.i == data_element->i && 
	p->info.j == data_element->j) {

        temp=p;
	data_element->dd = temp->info.dd;
	ipp = p->prev_node;
        p=p->next_node;  
	p->prev_node=ipp;
	goto NEXT;
    }
 
    temp=p->next_node;
    tp = p;
    tn = temp->next_node;

    while (temp->next_node != NULL) {

        if (temp->info.i == data_element->i && 
		temp->info.j == data_element->j) {

            if (tp != NULL) {
		if (tp->next_node != NULL) {
		   tp->next_node = temp->next_node;
		}
	    }
            if (tn != NULL) {
		tn->prev_node = temp->prev_node;
	    }
	    goto NEXT;
        }
	tp = temp;
        temp=temp->next_node;
	tn = temp->next_node;
    }
 
    if (temp->info.i == data_element->i && 
	temp->info.j == data_element->j) {
	data_element->dd = temp->info.dd;

        temp->prev_node->next_node=NULL;
	goto NEXT;
    }

NEXT:
    return p;
}
 
