LDAT get_info(node *begin,LDAT data_element)
{
    node *temp;
    LDAT nulinfo;

    nulinfo.i = -1; nulinfo.j = -1;
    if (begin == NULL) {
        return nulinfo;
    }

    if (begin->next_node == NULL) {  

        if (begin->info.i == data_element.i && 
		begin->info.j == data_element.j) {
            return begin->info;
        } else {
            return nulinfo;
        }
    }
    

    if (begin->info.i == data_element.i && 
		begin->info.j == data_element.j) {
        return begin->info;
    }
 
    temp = begin->next_node;

    while (temp->next_node != NULL) {

        if (temp->info.i == data_element.i && 
		temp->info.j == data_element.j) {
            return temp->info;
        }
        temp=temp->next_node;
    }
 
    if (temp->info.i == data_element.i && 
		temp->info.j == data_element.j) {

        return temp->info;
    }

    return nulinfo;
}
 
void delC(arr,n,value)
int *arr;
int *n;
int value;
{
    int i,j,k;

    k = -1;

    for (i=0; i < *n; i++) {
	if (arr[i] == value) {
	   k = i;
	}
    }

    if (k == -1) return;

    (*n)--;
    for (i=k; i < *n; i++) {
	arr[i] = arr[i+1];
    }
}

void repC(arr,n,value,nval)
int *arr;
int *n;
int value;
int nval;
{
    int i;

    for (i=0; i < *n; i++) {
	if (arr[i] == value) {
	   arr[i] = nval;
	}
    }

}

void redC(arr,n,nval)
int *arr;
int *n;
int nval;
{
    int i,j,frst,nn;


    frst = -1;
    i = 0;

    while (i < *n) {
	if (arr[i] == nval) {
	   if (frst != -1) {
		nn = (*n) - 1;
		for (j=i; j < nn; j++) {
		   arr[j] = arr[j+1];
		}
		(*n)--;
	   } else {
		frst = 0;
	   }
	}
	i++;
    }

}

void delArr(arr,n,value)
LDAT *arr;
int *n;
int value;
{
    int i;

    (*n)--;
    for (i=value; i < *n; i++) {
	arr[i] = arr[i+1];
    }
}

