void Trans_Coo(Tri2,coo)
TRISTRU *Tri2;
double *coo;
{
   int i,j;
   float pt[3];
   double R1[4][4],R2[4][4],t[3];

   if (!allConf(&Tri2->coo,ndocka)) exit(-1);

   for (j = 0; j<3; j++ ) {
	t[j] = Tri2->t[j];
   }

   for (i=0; i<ndocka; i++) {
     for (j=0; j<3; j++) {
	Tri2->coo[i*3+j] = t[j] + coo[i*3+j];
     }
   }

   for (i=0; i<4; i++) 
     for (j=0; j<4; j++) R1[i][j] = Tri2->R1[i][j];

   for (i=0; i<ndocka; i++) {
     for (j=0; j<3; j++) {
	pt[j] = Tri2->coo[i*3+j];
     }

     xyzROT4(R1,pt);

     for (j=0; j<3; j++) {
	Tri2->coo[i*3+j] = pt[j];
     }
   }

   for (i=0; i<4; i++) 
     for (j=0; j<4; j++) R2[i][j] = Tri2->R2[i][j];


   for (i=0; i<ndocka; i++) {
     for (j=0; j<3; j++) {

	pt[j] = (float) Tri2->coo[i*3+j];
     }

     xyzROT4(R2,pt);

     for (j=0; j<3; j++) {
	Tri2->coo[i*3+j] = (double) pt[j];
     }
   }

   OHreal(Tri2);

}

