void Compress_Tri_L()
{
   int i,ntritmp,tut,n;
   double dd;

   ntritmp = -1;

   for (i = 0; i < ntris; i++) {
	if (TriArr[i] != NULL) {
	   ntritmp++;
	   TriArrCopy[ntritmp] = TriArr[i];
	}
   }

   ntritmp++;
   ntris = ntritmp;

   for (i = 0; i < ntris; i++) {
	TriArr[i] = TriArrCopy[i];
   }
}

void Compress_Tri_List()
{
   int i,ntritmp,tut,n;
   double dd;

   if (ntris < MAXSOL) return;

   ntritmp = -1;

   n = ntris+1;
   tut = (ntris+1) % 2;
   if (tut > 0) n--;

   for (i = 0; i < n; i = i + 2 ) {
	if (TriArr[i] == NULL && TriArr[i+1] != NULL) {
	   ntritmp++;
	   TriArrCopy[ntritmp] = TriArr[i+1];
	   continue;
	}
	if (TriArr[i] != NULL && TriArr[i+1] == NULL) {
	   ntritmp++;
	   TriArrCopy[ntritmp] = TriArr[i];
	   continue;
	}
	if (TriArr[i] == NULL && TriArr[i+1] == NULL) continue;
	if (TriArr[i] != NULL && TriArr[i+1] != NULL) {
	   if (TriArr[i]->coo == NULL || TriArr[i+1]->coo == NULL) continue;
	}
	dd = dst(TriArr[i],TriArr[i+1]);
	if (dd <= 0.000001) {
	    TriSafeFree(i+1);
	    safefree((void **)&TriArr[i+1]);
	    TriArr[i+1] = NULL;
	    ntritmp++;
	    TriArrCopy[ntritmp] = TriArr[i];
	    TriArr[i] = NULL;
	} else {
	    ntritmp++;
	    TriArrCopy[ntritmp] = TriArr[i];
	    TriArr[i] = NULL;
	    ntritmp++;
	    TriArrCopy[ntritmp] = TriArr[i+1];
	    TriArr[i+1] = NULL;
	}
   }

   ntris = ntritmp;

   for (i = 0; i < ntris+1; i++) {
	TriArr[i] = TriArrCopy[i];
   }
}

