void Compress_Ligtyp_List()
{
   int i,j,k,lint,CARR[MXCONFARR];

   for (i=0; i < lintconf; i++) {
	ligtyp[i]->nconf = 1;
	CARR[0] = i;
	for (j=1; j < ntrif; j++) {
	   k = i+j*lintconf;
	   if (ligtyp[k] != NULL) {
	   if ((ligtyp[i]->ORI[0] == ligtyp[k]->ORI[0]) &&
		(ligtyp[i]->ORI[1] == ligtyp[k]->ORI[1]) &&
		(ligtyp[i]->ORI[2] == ligtyp[k]->ORI[2]) ) {

		if (ligtyp[i]->nconf < MXCONFARR) {
		   CARR[ligtyp[i]->nconf] = j;
		   ligtyp[i]->nconf++;
		   safefree((void **)&ligtyp[k]);
		   ligtyp[k] = NULL;
		}
	   }
	   }
	}
	ligtyp[i]->ConfArr = (int *) malloc(ligtyp[i]->nconf*sizeof(int));
	for (j=0; j < ligtyp[i]->nconf; j++) {
	   ligtyp[i]->ConfArr[j] = CARR[j];
	}
   }

   lint = 0;
   for (i=0; i < lintp; i++) {
	if (ligtyp[i] != NULL) {
	   if (i != 0) {
		ligtyp[lint] = ligtyp[i];
	   }
	   lint++;
	}
   }

   lintp = lint;
//  fprintf(stderr,"Compress_ligtyp_list: lintp %d\n\n",lintp);
}

