int CheckTimed()
{
  int fd,size,itel,itelm;
  struct stat buf;
  FILE *f;

  itel = 0; itelm = 0;

  while (1) {

	f = fopen("/tmp/yo","r");

	itelm++;

	if (f != NULL) {
	   fd = fileno(f);
	   fstat(fd, &buf);
	   size = buf.st_size;
	   fclose(f);
	   if (size > 0) {
	   	unlink("/tmp/yo");
		return(1);
	   }
	}

	if (itelm > 100) {
	   sleep(1); itel++;

	   if (itel > 10) return(0);
	}
  }

}

int AddHs(FILEOP *fileop,int isdfopt)
{
  int i,ii,in,n,fd,istat,endmol,size,pid,status;
  struct stat buf;
  char linstr[MAXFILELEN];
  char str[MAXFILELEN];
  char strh[MAXFILELEN];
  char *string;
  FILE *f,*ff,*fdest;
  size_t nl;

  if (getob()) {


   switch (isdfopt) {
   case 0:
   case 1:
	if (ChkAddHs(&COO[istruct]->memfil)) {
	   strcpy(cmdstr, "No need to add hydrogens");
	   if (DoCan(event.xbutton.x_root,event.xbutton.y_root,cmdstr, 0)) {}
	   return(0);
	}
	break;
   case 2:
	break;
   case 3:
	break;
   default:
	break;
   }

#if defined(VMS) || defined(UNDERSC)
   curs(&ONE);
#else
#ifdef CRAY
   CURS(&ONE);
#else
   curs_(&ONE);
#endif
#endif

   getstemp(stemp);

   switch (isdfopt) {
   case 0:
	strcat(stemp,"+polH.sdf");
	break;
   case 1:
	strcat(stemp,"+H.sdf");
	break;
   case 2:
	strcat(stemp,"+conv.sdf");
	break;
   case 3:
	strcat(stemp,"+3D.sdf");
	break;
   default:
	break;
   }

   fdest = fopen(stemp,"w");
   fclose(fdest);

   for (ii=0; ii < fileop->nfiles; ii++) {

	mfdata->imol = ii + 1;
	fileop->currl = GetLineIndx(ii,fileop);

	endmol = GetLineIndx(ii+1,fileop);
	if (endmol == -1) endmol = fileop->nlines + 1;

	n = endmol - fileop->currl;

	sprintf(str,"/tmp/%07d.mol",ii);
	sprintf(strh,"/tmp/%07d+h.mol",ii);

	f = fopen(str,"w");

	if (f != NULL) {
	   for (in=0; in < n; in++) {
		nline(linstr);
		fputs(linstr,f);
		fputs("\n",f);
	   }

	   fclose(f);
	}

	if (access("/tmp/yo",F_OK) == 0) unlink("/tmp/yo");

	switch (OBloc) {
	case 1:
	   sprintf(cmdstr,
		"%sobabel %s -i mol %s -O %s > /dev/null 2> /tmp/yo &", 
		exepath[0],sdfcopt[isdfopt],str,strh);
	   break;

	case 2:
	   sprintf(cmdstr,
		"/usr/bin/obabel %s -i mol %s -O %s > /dev/null 2> /tmp/yo &",
		sdfcopt[isdfopt],str,strh);
	   break;
	case 0:
	   sprintf(cmdstr,
	   "/usr/local/bin/obabel %s -i mol %s -O %s > /dev/null 2> /tmp/yo &",
		sdfcopt[isdfopt],str,strh);
	   break;
	default:
	   break;
	}

	excmd(cmdstr,&pid);

	if (CheckTimed()) {

		if (access(str,F_OK) == 0) unlink(str);
		ff = fopen(strh,"r");

		if (ff != NULL) {
		   fd = fileno(f);
		   fstat(fd, &buf);
		   size = buf.st_size;
		   string = malloc(sizeof(char)*size+1);
		   nl = fread(string, sizeof(char), size, ff);
		   string[nl] = '\0';
		   fclose(ff);
		   if (access(strh,F_OK) == 0) unlink(strh);

		   if (nl != 0) {
			fdest = fopen(stemp,"a");

			fputs(string,fdest);
			fputs("$$$$\n",fdest);
			fflush(fdest);
			safefree((void **)&string);
			fclose(fdest);
		   } else {
			safefree((void **)&string);
		   }

		} else {
		   fprintf(stderr,"error opening file %s\n",strh);
		   killob();
		}

	} else {
		killob();
		if (access(str,F_OK) == 0) unlink(str);
		if (access(strh,F_OK) == 0) unlink(strh);
		fprintf(stderr,"Error adding hydrogens: %s\n",strh);
	}



   }

#if defined(VMS) || defined(UNDERSC)
   curs(&ZERO);
#else
#ifdef CRAY
   CURS(&ZERO);
#else
   curs_(&ZERO);
#endif
#endif

   sprintf(molwstr, "Wrote file %s",stemp);
   StatusStr(0,1);

  }

  return(1);
}

