  FILOP(&SEVENTYSEVEN,&ONE,&ONE,&ZERO,&ierr);
#else
  cpkeyw_();
  filop_(&SEVENTYSEVEN,&ONE,&ONE,&ZERO,&ierr);
#endif
#endif

